SELECT * FROM ( SELECT cu.Customer_Id,cu.contract_code,cu.Customer_Name,cu.Customer_Phone,cu.Customer_Email,cu.Customer_Company,cu.Customer_office,cu.Customer_Address, cm.Account_Balance,cm.Trade_Time,cu.Create_Time,u.User_Name FROM sys_customer_info_t cu LEFT JOIN sys_user_info_t u ON cu.Create_User = u.User_Id LEFT JOIN (SELECT mcu.Customer_Id,mcu.Trade_Time,ctr.Account_Balance FROM (SELECT Customer_Id,MAX(Trade_Time) AS Trade_Time FROM customer_trade_record GROUP BY Customer_Id) mcu LEFT JOIN customer_trade_record ctr ON mcu.Customer_Id=ctr.Customer_Id AND ctr.Trade_Time=mcu.Trade_Time) cm ON cu.Customer_Id=cm.Customer_Id
(u.City_Id IS NULL OR u.City_Id=0) AND u.province_id=#{provinceId}
AND cu.contract_code LIKE '%${contractCode}%'
UNION ALL SELECT cu.Customer_Id,cu.contract_code,cu.Customer_Name,cu.Customer_Phone,cu.Customer_Email,cu.Customer_Company,cu.Customer_office,cu.Customer_Address, cm.Account_Balance,cm.Trade_Time,cu.Create_Time,u.User_Name FROM sys_customer_info_t cu LEFT JOIN sys_user_info_t u ON cu.Create_User = u.User_Id LEFT JOIN (SELECT mcu.Customer_Id,mcu.Trade_Time,ctr.Account_Balance FROM (SELECT Customer_Id,MAX(Trade_Time) AS Trade_Time FROM customer_trade_record GROUP BY Customer_Id) mcu LEFT JOIN customer_trade_record ctr ON mcu.Customer_Id=ctr.Customer_Id AND ctr.Trade_Time=mcu.Trade_Time) cm ON cu.Customer_Id=cm.Customer_Id
u.City_Id IS NOT NULL AND u.City_Id=#{cityId}
AND cu.contract_code LIKE '%${contractCode}%'
)t ORDER BY t.Create_Time DESC
limit ${page.start},${page.limit}

SELECT count(*) FROM ( SELECT cu.Customer_Id,cu.contract_code,cu.Customer_Name,cu.Customer_Phone,cu.Customer_Email,cu.Customer_Company,cu.Customer_office,cu.Customer_Address, cm.Account_Balance,cm.Trade_Time,cu.Create_Time,u.User_Name FROM sys_customer_info_t cu LEFT JOIN sys_user_info_t u ON cu.Create_User = u.User_Id LEFT JOIN (SELECT mcu.Customer_Id,mcu.Trade_Time,ctr.Account_Balance FROM (SELECT Customer_Id,MAX(Trade_Time) AS Trade_Time FROM customer_trade_record GROUP BY Customer_Id) mcu LEFT JOIN customer_trade_record ctr ON mcu.Customer_Id=ctr.Customer_Id AND ctr.Trade_Time=mcu.Trade_Time) cm ON cu.Customer_Id=cm.Customer_Id
(u.City_Id IS NULL OR u.City_Id=0) AND u.province_id=#{provinceId}
AND cu.contract_code LIKE '%${contractCode}%'
UNION ALL SELECT cu.Customer_Id,cu.contract_code,cu.Customer_Name,cu.Customer_Phone,cu.Customer_Email,cu.Customer_Company,cu.Customer_office,cu.Customer_Address, cm.Account_Balance,cm.Trade_Time,cu.Create_Time,u.User_Name FROM sys_customer_info_t cu LEFT JOIN sys_user_info_t u ON cu.Create_User = u.User_Id LEFT JOIN (SELECT mcu.Customer_Id,mcu.Trade_Time,ctr.Account_Balance FROM (SELECT Customer_Id,MAX(Trade_Time) AS Trade_Time FROM customer_trade_record GROUP BY Customer_Id) mcu LEFT JOIN customer_trade_record ctr ON mcu.Customer_Id=ctr.Customer_Id AND ctr.Trade_Time=mcu.Trade_Time) cm ON cu.Customer_Id=cm.Customer_Id
u.City_Id IS NOT NULL AND u.City_Id=#{cityId}
AND cu.contract_code LIKE '%${contractCode}%'
)t

SELECT cr.Customer_Id,cr.Trade_No,cr.Trade_Type,cr.Trade_Amount,cr.Account_Balance,cr.Trade_Remark,cr.Trade_Time,cr.Create_User,cr.Create_Time,
cu.Customer_Name,cu.Customer_Phone,cu.Customer_Email,cu.Customer_Company,cu.contract_code,cu.Customer_office,cu.Customer_Address,su.User_Name
FROM customer_trade_record cr LEFT JOIN sys_customer_info_t cu ON cr.Customer_Id=cu.Customer_Id
LEFT JOIN sys_user_info_t su ON cr.Create_User=su.User_Id

	cr.Create_User IS NOT NULL AND cr.Create_User!=0 AND cr.Customer_Id=#{customerId}

		AND cr.Trade_Type =#{tradeType}


		AND cr.Trade_Time LIKE '%${startTime}%'


		AND cr.Trade_Time >= #{startTime} AND cr.Trade_Time <= #{endTime}




ORDER BY cr.Trade_Time DESC

	limit ${page.start},${page.limit}

SELECT count(*) FROM customer_trade_record cr LEFT JOIN sys_customer_info_t cu ON cr.Customer_Id=cu.Customer_Id

	cr.Customer_Id=#{customerId}

		AND cr.Trade_Type =#{tradeType}


		AND cr.Trade_Time LIKE '%${startTime}%'


		AND cr.Trade_Time >= #{startTime} AND cr.Trade_Time <= #{endTime}
